$(window).load(function(){$('body').prepend("<div id='viewport'><div id='ruler'><span class='small'></span></div><div id='sizeInfo'></div></div>");offset=15;$viewport=$('#viewport');$sizeinfo=$('#sizeInfo');$spacer=$('<div />').css('height',$viewport.outerHeight());$viewport.before($spacer);if($(document).height()>$(window).height()&&($.browser.mozilla||$.browser.msie)){$sizeinfo.html($(window).width()+offset+'px &#9666;&#9656;')}else{$sizeinfo.html($(window).width()+'px &#9666;&#9656;')}$(window).resize(function(){if($(document).height()>$(window).height()&&($.browser.mozilla||$.browser.msie)){$sizeinfo.html($(window).width()+offset+'px &#9666;&#9656;')}else{$sizeinfo.html($(window).width()+'px  &#9666;&#9656;')}});$(document).keydown(function(e){if(e.keyCode==84){$viewport.toggle();$spacer.toggle()}})});